pm.test("User without permission denied", function () {
    pm.response.to.have.status(403);
});

pm.test("Permission validation", function () {
    const responseJson = pm.response.json();
    const errorMsg = (responseJson.error || responseJson.message || "").toLowerCase();
    pm.expect(errorMsg).to.satisfy(msg => 
        msg.includes("permission") || msg.includes("forbidden") || msg.includes("access")
    );
});

// BUG DETECTION
pm.test("SECURITY CHECK: Permission enforcement", function () {
    if (pm.response.code === 200) {
        console.log("SECURITY BUG: Regular user can change invoice status!");
        pm.globals.set("security_bug_permission", "found");
    }
});
